 function terbilang($x) { $angka = array('', "\163\x61\x74\x75", "\x64\165\x61", "\164\151\147\x61", "\145\155\160\x61\164", "\x6c\x69\155\x61", "\x65\156\141\x6d", "\x74\165\152\x75\x68", "\x64\x65\154\141\160\141\x6e", "\163\x65\155\142\151\x6c\x61\156", "\x73\145\x70\x75\154\165\150", "\x73\x65\142\145\x6c\x61\163"); if ($x < 12) { return "\x20" . $angka[$x]; } elseif ($x < 20) { return terbilang($x - 10) . "\x20\142\x65\x6c\141\163"; } elseif ($x < 100) { return terbilang($x / 10) . "\x20\160\x75\x6c\165\150" . terbilang($x % 10); } elseif ($x < 200) { return "\x73\145\162\x61\164\x75\x73" . terbilang($x - 100); } elseif ($x < 1000) { return terbilang($x / 100) . "\x20\162\x61\164\165\x73" . terbilang($x % 100); } elseif ($x < 2000) { return "\163\x65\x72\x69\142\165" . terbilang($x - 1000); } elseif ($x < 1000000) { return terbilang($x / 1000) . "\40\x72\x69\x62\165" . terbilang($x % 1000); } elseif ($x < 1000000000) { return terbilang($x / 1000000) . "\x20\x6a\165\164\141" . terbilang($x % 1000000); } elseif ($x < 1000000000000) { return terbilang($x / 1000000000) . "\x20\x6d\151\154\154\x69\x61\162" . terbilang($x % 1000000000); } elseif ($x < 1000000000000000) { return terbilang($x / 1000000000000) . "\x20\x74\x72\x69\154\151\165\156" . terbilang($x % 1000000000000); } } ?>